private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

    int fechaDia = this.jComboBox3.getSelectedIndex();
    int fechaMes = this.jComboBox4.getSelectedIndex();
    int fechaAño = this.jComboBox5.getSelectedIndex();
    if (fechaMes >= 6){
                    
                    JOptionPane.showMessageDialog(rootPane, "Generar Turno de Guardia para la Fecha Ingresada","Crear Guardia", WIDTH);
                    CrearGuardiaMedica cgm = new CrearGuardiaMedica(fechaDia, fechaMes, fechaAño);
                    cgm.setVisible(true);
                               
    }
    else{
        JOptionPane.showMessageDialog(rootPane, "Ya se Genero Turno de Guardia para la Fecha Ingresada","Error", WIDTH);
    }
    
}//GEN-LAST:event_jButton1ActionPerformed

